function Options({question , dispatch , answer}) {
    return (
          <div className="options">
            {question.options.map((option , index) => (<button className="btn btn-option" onClick={()=>dispatch({type : newAnswer , payload : index})} key={option}>{option}</button>))}
            </div>
    )
}

export default Options
